import{_ as a,c as e,o as r,O as s}from"./chunks/framework.d7e37bdc.js";const C=JSON.parse('{"title":"👩‍💻办公软件","description":"","frontmatter":{},"headers":[],"relativePath":"office.md","filePath":"office.md","lastUpdated":1676957251000}'),o={name:"office.md"},t=s('<h1 id="👩‍💻办公软件" tabindex="-1">👩‍💻办公软件 <a class="header-anchor" href="#👩‍💻办公软件" aria-label="Permalink to &quot;👩‍💻办公软件&quot;">​</a></h1><h3 id="办公套件" tabindex="-1">办公套件 <a class="header-anchor" href="#办公套件" aria-label="Permalink to &quot;办公套件&quot;">​</a></h3><h4 id="wps" tabindex="-1">WPS <a class="header-anchor" href="#wps" aria-label="Permalink to &quot;WPS&quot;">​</a></h4><p><a href="https://www.wps.cn/" target="_blank" rel="noreferrer">WPS Office</a> 是由 <a href="https://www.kingsoft.com/" target="_blank" rel="noreferrer">金山软件</a> 发布的一款办公软件，用于办公软件最常用的文字编辑、电子表格、演示文稿等功能。</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">yay</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">wps-office-cn</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ttf-wps-fonts</span></span></code></pre></div><h4 id="libreoffice" tabindex="-1">LibreOffice <a class="header-anchor" href="#libreoffice" aria-label="Permalink to &quot;LibreOffice&quot;">​</a></h4><p><a href="https://zh-cn.libreoffice.org/" target="_blank" rel="noreferrer">LibreOffice</a> 是由 <a href="https://www.documentfoundation.org/" target="_blank" rel="noreferrer">文档基金会</a> 开发的自由及开放源代码的办公室套件。LibreOffice 是一款功能强大的办公软件，默认使用 ODF 开放文档格式（OpenDocument Format），并支持 <em>.docx、</em>.xlsx、*.pptx 等其它格式。</p><p>它包含了 Writer、Calc、Impres、Draw、Base 以及 Math 等组件，可用于处理文本文档、电子表格、演示文稿、绘图、数据库管理以及公式编辑。</p><p>安装 <a href="https://archlinux.org/packages/extra/x86_64/libreoffice-still/" target="_blank" rel="noreferrer">LibreOffice</a> 以及 <a href="https://archlinux.org/packages/extra/any/libreoffice-still-zh-cn/" target="_blank" rel="noreferrer">其中文语言包</a>：</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pacman</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libreoffice-still</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">libreoffice-still-zh-cn</span></span></code></pre></div><h4 id="foxit-pdf-reader" tabindex="-1">Foxit PDF Reader <a class="header-anchor" href="#foxit-pdf-reader" aria-label="Permalink to &quot;Foxit PDF Reader&quot;">​</a></h4><p><a href="https://www.foxitsoftware.com/products/pdf-reader/" target="_blank" rel="noreferrer">Foxit PDF Reader</a>是福建福昕软件开发股份有限公司推出的一款轻量级PDF编辑器，是目前笔者在Linux平台下使用过免费且最好用的PDF编辑器。Foxit PDF Reader提供常规的PDF注释功能：段落高亮、区域高亮、下划线、波浪线、图形、删除线和备注等标记，也提供许多其他平台付费PDF编辑器提供的打字机、文本框、笔迹等轻量级PDF编辑。加载和保存PDF速度较Libre Office Draw快，打字机功能较KDE Okular支持非拉丁字符输入，是PDF笔记文档注释的好帮手。</p><p>安装<a href="https://aur.archlinux.org/packages/foxitreader/" target="_blank" rel="noreferrer">Foxit PDF Reader</a>:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">yay</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">foxitreader</span></span></code></pre></div><h3 id="markdown-编辑器" tabindex="-1">Markdown 编辑器 <a class="header-anchor" href="#markdown-编辑器" aria-label="Permalink to &quot;Markdown 编辑器&quot;">​</a></h3><h4 id="typora" tabindex="-1">Typora <a class="header-anchor" href="#typora" aria-label="Permalink to &quot;Typora&quot;">​</a></h4><p><a href="https://typora.io/" target="_blank" rel="noreferrer">Typora</a> 是一款由 Abner Lee（上海人）开发的轻量级 Markdown 编辑器。</p><p>Typora 没有采用源代码和预览双栏显示的方式，而是采用所见即所得的编辑方式，实现了即时预览的功能，但也可切换至源代码编辑模式。在编辑时，除了通过传统的 Markdown 代码的方式来实现富文本之外，Typora 支持通过菜单栏或者鼠标右键选取命令的方式来实现富文本，也支持通过快捷键的方式插入。Typora 也支持通过以 TeX 的格式来插入行间公式和行内公式。在完成编辑后导出文件时，Typora 支持以 PDF 或 Html 的形式导出，如果安装了 Pandoc，也能够以 Word、RTF、MediaWiki、LaTeX 等形式导出。</p><p>安装 <a href="https://aur.archlinux.org/packages/typora/" target="_blank" rel="noreferrer">Typora</a>EULA / cn / aur 以及 <a href="https://archlinux.org/packages/community/x86_64/pandoc/" target="_blank" rel="noreferrer">Pandoc</a>：</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pacman</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">typora</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">pandoc</span></span></code></pre></div><p>值得注意的是，目前typora软件已经开始收费。如果有能力购买的话可以进行购买。对于囊中羞涩的学生党或其他不愿购买的用户，我们可以选择使用typora的最后一个免费版本（注意：不是盗版），安装方式如下</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">yay</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-S</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">typora-free</span></span></code></pre></div><h1 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to &quot;&quot;">​</a></h1>',23),n=[t];function l(p,c,i,h,d,f){return r(),e("div",null,n)}const b=a(o,[["render",l]]);export{C as __pageData,b as default};
